mapper.select:
select * from
(select * from nx_community_goods


        and nx_cg_card_id = #{cardId}


        and nx_cg_community_id = #{commId}


        and nx_cg_distributer_id = #{disId}


        and nx_cg_nx_goods_id = #{goodsId}


        and nx_cg_goods_type = #{type}


        and nx_cg_cfg_goods_father_id = #{cgFatherId}



        and nx_cg_goods_price is not null and nx_cg_goods_price > 0


        and nx_cg_goods_two_price is not null and nx_cg_goods_two_price > 0


        and nx_cg_goods_three_price is not null and nx_cg_goods_three_price > 0



order by nx_cg_goods_py, nx_cg_goods_pinyin

    limit #{offset}, #{limit}

)as cg
left join nx_community_standard as cs on cs.nx_CS_comm_goods_id = cg.nx_community_goods_id
left join nx_community_alias as ca on ca.nx_CA_com_goods_id = cg.nx_community_goods_id
order by cg.nx_cg_goods_py, cg.nx_cg_goods_pinyin, cg.nx_cg_nx_goods_id, cs.nx_community_standard_id

select * from nx_community_goods as cg left join nx_community as c on c.nx_community_id = cg.nx_cg_community_id left join nx_distributer as dis on cg.nx_cg_distributer_id = dis.nx_distributer_id left join nx_community_supplier as ncs on ncs.nx_community_supplier_id = cg.nx_cg_community_supplier_id
and cg.nx_cg_cfg_goods_father_id = #{fatherId}
limit #{offset}, #{limit}
order by cg.nx_cg_goods_sort

select count(*) from nx_community_goods


        and nx_cg_cfg_goods_father_id = #{fatherId}


        and nx_cg_goods_type = #{type}


        and nx_cg_goods_price is not null and nx_cg_goods_price > 0


        and nx_cg_goods_two_price is not null and nx_cg_goods_two_price > 0


        and nx_cg_goods_three_price is not null and nx_cg_goods_three_price > 0

select * from (select * from nx_community_goods
and nx_cg_cfg_goods_father_id = #{cgFatherId}
and nx_cg_goods_price is not null and nx_cg_goods_price > 0
and nx_cg_goods_two_price is not null and nx_cg_goods_two_price > 0
and nx_cg_goods_three_price is not null and nx_cg_goods_three_price > 0
order by nx_cg_goods_py, nx_cg_goods_pinyin
limit #{offset}, #{limit}
)as cg left join nx_community_standard as cs on cs.nx_CS_comm_goods_id = cg.nx_community_goods_id left join nx_community_alias as ca on ca.nx_CA_com_goods_id = cg.nx_community_goods_id left join nx_restraunt_com_goods as rc on rc.nx_RCG_com_goods_id = cg.nx_community_goods_id and nx_RCG_restraunt_father_id = #{resFatherId} order by cg.nx_cg_goods_py, cg.nx_cg_goods_pinyin, cg.nx_cg_nx_goods_id
